## 4. Conversion Layer Implementation (PetriNetConverter.cpp)

```cpp
#include "PetriNetConverter.h"
#include <iostream>
#include <regex>
#include <cuda_runtime.h>

bool PetriNetConverter::convertToCudaStructures(
    const std::shared_ptr<PetriNet>& petriNet,
    std::vector<CPUPlace>& cpuPlaces,
    std::vector<CPUTransition>& cpuTransitions
) {
    if (!petriNet) {
        std::cerr << "Error: Null Petri net" << std::endl;
        return false;
    }

    // Create mappings from string IDs to integer indices
    std::unordered_map<std::string, int> placeIdMap;
    std::unordered_map<std::string, int> transitionIdMap;

    // Convert places
    convertPlaces(petriNet, cpuPlaces, placeIdMap);

    // Convert transitions
    convertTransitions(petriNet, cpuTransitions, transitionIdMap);

    // Connect arcs between places and transitions
    connectArcs(petriNet, cpuPlaces, cpuTransitions, placeIdMap, transitionIdMap);

    return true;
}

void PetriNetConverter::convertPlaces(
    const std::shared_ptr<PetriNet>& petriNet,
    std::vector<CPUPlace>& cpuPlaces,
    std::unordered_map<std::string, int>& placeIdMap
) {
    int placeIndex = 0;

    for (const auto& [id, place] : petriNet->places) {
        CPUPlace cpuPlace;
        cpuPlace.id = placeIndex;
        cpuPlace.tokenCount = place->initialMarking;

        // Initialize tokens (all with age 0.0)
        for (int i = 0; i < place->initialMarking; i++) {
            cpuPlace.tokens.push_back(0.0f);
        }

        // Store the mapping from string ID to index
        placeIdMap[id] = placeIndex;
        cpuPlaces.push_back(cpuPlace);
        placeIndex++;
    }
}

void PetriNetConverter::convertTransitions(
    const std::shared_ptr<PetriNet>& petriNet,
    std::vector<CPUTransition>& cpuTransitions,
    std::unordered_map<std::string, int>& transitionIdMap
) {
    int transitionIndex = 0;

    for (const auto& [id, transition] : petriNet->transitions) {
        CPUTransition cpuTransition;
        cpuTransition.id = transitionIndex;
        cpuTransition.urgent = transition->urgent;

        // Parse distribution
        parseDistribution(transition->distribution, cpuTransition.distribution);

        // Set distribution parameters
        if (transition->distribution == "constant") {
            cpuTransition.distribution.type = CONSTANT;
            cpuTransition.distribution.a = transition->value;
        } else if (transition->distribution == "uniform") {
            cpuTransition.distribution.type = UNIFORM;
            cpuTransition.distribution.a = transition->a;
            cpuTransition.distribution.b = transition->b;
        }

        // Store the mapping
        transitionIdMap[id] = transitionIndex;
        cpuTransitions.push_back(cpuTransition);
        transitionIndex++;
    }
}

void PetriNetConverter::connectArcs(
    const std::shared_ptr<PetriNet>& petriNet,
    std::vector<CPUPlace>& cpuPlaces,
    std::vector<CPUTransition>& cpuTransitions,
    const std::unordered_map<std::string, int>& placeIdMap,
    const std::unordered_map<std::string, int>& transitionIdMap
) {
    // Map of transport arcs by ID for later pairing
    std::unordered_map<std::string, Arc> transportArcs;

    // Process all arcs
    for (const auto& arc : petriNet->arcs) {
        // Determine if source is a place or transition
        bool sourceIsPlace = placeIdMap.find(arc->sourceId) != placeIdMap.end();

        if (sourceIsPlace) {
            // This is an input arc to a transition
            int placeIndex = placeIdMap.at(arc->sourceId);
            int transIndex = transitionIdMap.at(arc->targetId);

            Arc inputArc;
            inputArc.place = &cpuPlaces[placeIndex];

            // Determine arc type
            if (arc->type == "normal") {
                inputArc.type = INPUT;
            } else if (arc->type == "inhibitor") {
                inputArc.type = INHIBITOR;
            } else if (arc->type == "transport") {
                inputArc.type = TRANSPORT;
                // Store for later pairing with output transport arc
                if (!arc->transportId.empty()) {
                    transportArcs[arc->transportId] = inputArc;
                }
            }

            // Parse weight (default to 1)
            inputArc.weight = 1;

            // Parse timing constraints
            float minTime = 0.0f;
            float maxTime = MAXFLOAT;
            parseTimingConstraint(arc->inscription, minTime, maxTime);
            inputArc.timings[0] = minTime;
            inputArc.timings[1] = maxTime;

            // Add to transition's input arcs
            cpuTransitions[transIndex].inputArcs.push_back(inputArc);
        } else {
            // This is an output arc from a transition
            int transIndex = transitionIdMap.at(arc->sourceId);
            int placeIndex = placeIdMap.at(arc->targetId);

            OutputArc outputArc;
            outputArc.output = &cpuPlaces[placeIndex];

            // Parse weight (default to 1)
            outputArc.weight = 1;

            // Check if this is a transport arc
            outputArc.isTransport = (arc->type == "transport");

            // Add to transition's output arcs
            cpuTransitions[transIndex].outputArcs.push_back(outputArc);
        }
    }
}

bool PetriNetConverter::parseTimingConstraint(const std::string& inscription, float& minTime, float& maxTime) {
    // Default values
    minTime = 0.0f;
    maxTime = MAXFLOAT;

    if (inscription.empty()) {
        return true;
    }

    // Parsing patterns like [0,inf), [1,5]
    std::regex pattern(R"(\[(\d+(?:\.\d+)?),\s*(inf|(?:\d+(?:\.\d+)?))\))");
    std::smatch matches;

    if (std::regex_search(inscription, matches, pattern)) {
        minTime = std::stof(matches[1].str());
        if (matches[2] == "inf") {
            maxTime = MAXFLOAT;
        } else {
            maxTime = std::stof(matches[2].str());
        }
        return true;
    }

    return false;
}

bool PetriNetConverter::parseDistribution(const std::string& distributionStr, Distribution& dist) {
    if (distributionStr == "constant") {
        dist.type = CONSTANT;
        return true;
    } else if (distributionStr == "uniform") {
        dist.type = UNIFORM;
        return true;
    }

    // Default to constant
    dist.type = CONSTANT;
    dist.a = 0.0f;
    return false;
}

bool PetriNetConverter::transferToDevice(
    const std::vector<CPUPlace>& cpuPlaces,
    const std::vector<CPUTransition>& cpuTransitions,
    Place** devicePlaces,
    Transition** deviceTransitions,
    int* numPlaces,
    int* numTransitions
) {
    *numPlaces = cpuPlaces.size();
    *numTransitions = cpuTransitions.size();

    // Allocate device memory for places
    cudaError_t error = cudaMalloc((void**)devicePlaces, sizeof(Place) * cpuPlaces.size());
    if (error != cudaSuccess) {
        std::cerr << "Failed to allocate device memory for places: " << cudaGetErrorString(error) << std::endl;
        return false;
    }

    // Copy place data to device
    for (size_t i = 0; i < cpuPlaces.size(); i++) {
        Place hostPlace;
        hostPlace.id = cpuPlaces[i].id;
        hostPlace.tokenCount = cpuPlaces[i].tokenCount;

        // Copy tokens
        for (int j = 0; j < cpuPlaces[i].tokenCount && j < 8; j++) {
            hostPlace.tokens[j] = cpuPlaces[i].tokens[j];
        }

        // Copy place to device
        error = cudaMemcpy(&((*devicePlaces)[i]), &hostPlace, sizeof(Place), cudaMemcpyHostToDevice);
        if (error != cudaSuccess) {
            std::cerr << "Failed to copy place to device: " << cudaGetErrorString(error) << std::endl;
            cudaFree(*devicePlaces);
            return false;
        }
    }

    // Allocate device memory for transitions
    error = cudaMalloc((void**)deviceTransitions, sizeof(Transition) * cpuTransitions.size());
    if (error != cudaSuccess) {
        std::cerr << "Failed to allocate device memory for transitions: " << cudaGetErrorString(error) << std::endl;
        cudaFree(*devicePlaces);
        return false;
    }

    // Copy transition data to device
    for (size_t i = 0; i < cpuTransitions.size(); i++) {
        Transition hostTransition;
        hostTransition.id = cpuTransitions[i].id;
        hostTransition.distribution = cpuTransitions[i].distribution;
        hostTransition.urgent = cpuTransitions[i].urgent;
        hostTransition.inputArcsCount = cpuTransitions[i].inputArcs.size();
        hostTransition.outputArcsCount = cpuTransitions[i].outputArcs.size();

        // Copy input arcs (up to 5)
        for (size_t j = 0; j < cpuTransitions[i].inputArcs.size() && j < 5; j++) {
            hostTransition.inputArcs[j] = cpuTransitions[i].inputArcs[j];
        }

        // Copy output arcs (up to 5)
        for (size_t j = 0; j < cpuTransitions[i].outputArcs.size() && j < 5; j++) {
            hostTransition.outputArcs[j] = cpuTransitions[i].outputArcs[j];
        }

        // Copy transition to device
        error = cudaMemcpy(&((*deviceTransitions)[i]), &hostTransition, sizeof(Transition), cudaMemcpyHostToDevice);
        if (error != cudaSuccess) {
            std::cerr << "Failed to copy transition to device: " << cudaGetErrorString(error) << std::endl;
            cudaFree(*devicePlaces);
            cudaFree(*deviceTransitions);
            return false;
        }
    }

    return true;
}
```

